import React, {useEffect, useState } from 'react';
import { Routes, Route, Link} from "react-router-dom";
import { AppBar, Toolbar, Typography, InputBase, Box } from '@material-ui/core';
import Button from '@mui/material/Button';

import Home from './Components/Home/Home.jsx';
import LogIn from './Components/Login/Login.jsx';
import SignUp from './Components/Sign-Up/Sign-Up.jsx';
import Header from './Components/Header/Header.jsx';
import useStyles from './Components/Header/styles';

const App = () => {
    const [currentPage, setCurrentPage] = useState('');
    const classes = useStyles();
    
    return (
      <>
        <AppBar position="static">
            <Toolbar className={classes.toolbar}>
                <Button variant="outlined" sx={{color: 'white'}} href="/"> 
                   <Typography variant="subtitle1" sx={{fontWeight: 'bold'}}> Trivia Junkie </Typography>
                </Button> 
                <Box display="flex">
                    <div className={classes.search}>
                        <Button variant="primary" href="/login">Log in </Button>
                    </div>
                    <div className={classes.search}>
                        <Button variant="primary" href="/sign-up"> Sign up </Button>
                    </div>
                </Box>
            </Toolbar>
        </AppBar>

        
      <Routes> 
        <Route path="/" element={<Home/>} />
        <Route path="/login" element={<LogIn/>} />
        <Route path="/sign-up" element={<SignUp/>} />
      </Routes>
      </>
    );
}

export default App;
